<rss version="2.0" xmlns:atom="http://www.w3.org/2005/Atom">    <channel>        <title>Pertemuan Mata Kuliah</title>        <description></description><?php    include "config.php";        $query = "SELECT DATE_FORMAT(DATE_ADD(FROM_UNIXTIME(b.startdate, '%Y-%m-%d'), INTERVAL ((e.section - 1) * 7) DAY), '%d %b %y') as startdatesection,                  DATE_FORMAT(DATE_ADD(FROM_UNIXTIME(b.startdate, '%Y-%m-%d'), INTERVAL (((e.section - 1) * 7) + 6) DAY), '%d %b %y') as enddatesection,                 e.id as sectionid, e.section                FROM mdl_enrol AS a                    INNER JOIN mdl_course AS b ON a.courseid = b.id                    INNER JOIN mdl_user_enrolments AS c ON c.enrolid = a.id                    INNER JOIN mdl_user AS d ON d.id = c.userid                    INNER JOIN mdl_course_sections AS e ON e.course = b.id                    AND b.format = 'weeks' AND e.section > 0 AND b.id = '" . $_GET["courseid"] . "'                     AND d.id = '" . $_GET["userid"] . "'";        $result = mysql_query($query);    while ($row = mysql_fetch_array($result)) {?>        <item>            <link><?php echo $row["sectionid"]; ?></link>            <title><?php echo $row["startdatesection"] . " - " . $row["enddatesection"]; ?></title>            <description><?php echo "Minggu " . $row["section"]; ?></description>        </item><?php    }?>    </channel></rss><?php    mysql_close();?>